import React, { Component } from "react";
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import Dropdown from 'react-bootstrap/Dropdown';

class Form extends Component {
  // Setting the initial values of this.state.username and this.state.password
  state = {
    offer: "",
    quantity: "",
    request: "",
    requestquantity: ""


  };

  // handle any changes to the input fields
  handleInputChange = event => {
    // Pull the name and value properties off of the event.target (the element which triggered the event)
    const { name, value } = event.target;


    // Set the state for the appropriate input field

    this.setState({
      [name]: value
    });
  };


  // When the form is submitted, prevent the default event and alert the username and password
  handleFormSubmit = event => {
    event.preventDefault();
    alert(`Offer: ${this.state.offer}\nQuantity: ${this.state.quantity}\nRequest: ${this.state.request}\nRequestQuantity: ${this.state.request}`);
    this.setState({ offer: "", quantity: "", request: "", requestquantity: "" });
  };

  render() {
    return (

      <div className="table table-striped table-bordered" style={{
        borderRadius: '10px',
        border: '1px solid black',
        width: '50%',
        height: '50%',
        flex: 1,
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: '#AA8CBB',


      }}>

        <form className="makeofferform" style={{
          textAlign: "center",
          justifyContent: "center",
          alignItems: "center",
          // position: "absolute",
          flex: 1,
          right: 0,
          left: 0,
        }}>
          <label>Offer:</label>
          <input
            type="text"
            placeholder="Your Offer"
            name="offer"
            value={this.state.offer}
            onChange={this.handleInputChange}
          />
          <label>Quantity:</label>
          <input
            type="text"
            placeholder="Your Quantity"
            name="quantity"
            value={this.state.quantity}
            onChange={this.handleInputChange}
          />

          <Dropdown as={ButtonGroup}>
            <Button variant="success">Split Button</Button>

            <Dropdown.Toggle split variant="success" id="dropdown-split-basic" />

            <Dropdown.Menu>
              <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
              <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>

          <br></br>
          <br></br>
          <p>AND</p>
          <br></br>

          <label>Requesting:</label>
          <input
            type="text"
            placeholder="Your Request"
            name="request"
            value={this.state.request}
            onChange={this.handleInputChange}
          />

          <label>Request Quantity:</label>
          <input
            type="text"
            placeholder="Your Request Amount"
            name="requestquantity"
            value={this.state.requestquantity}
            onChange={this.handleInputChange}
          />
          <br></br>
          <br></br>



          <button onClick={this.handleFormSubmit}>Submit Offer!</button>
        </form>
      </div>


    );
  }
}
export default Form;


